"use client";
import { useList } from "@/context/ListContext";
import { getImgUrl } from "@/lib/imageUrl";
import Link from "next/link";

function fmtPrice(v: any) {
  const n = Number(v);
  if (!isFinite(n) || n <= 0) return "";
  return n.toLocaleString(undefined, { style: "currency", currency: "USD", maximumFractionDigits: 0 });
}

export default function ProductCard({ p }: { p: any }) {
  const { add, remove, has } = useList();
  const id = String(p._id ?? p.product_id ?? p.id ?? "");
  const name = p.name || p.title || id;
  const raw =
    p.images?.[0]?.url ||
    p.image?.url ||
    p.image_url ||
    p.imagePath ||
    p.image ||
    p.img ||
    "";

  const img = raw && typeof raw === "string" && raw.startsWith("/uploads")
    ? `/media${raw}`
    : getImgUrl(raw);
  const inList = has(id);
  const isSold = (p.stock && String(p.stock).toLowerCase() !== "in") || p.sold === true;

  return (
    <article className="group rounded-2xl border overflow-hidden bg-white hover:shadow transition">
      <Link href={`/rug/${id}`} className="block">
        <div className="relative aspect-[4/3] bg-gray-50">
          <img
            src={img}
            alt={name}
            title={img}
            className="w-full h-full object-cover"
            loading="lazy"
            onError={(e) => {
              try { console.warn("img error for", id, "->", img); } catch {}
              e.currentTarget.src = "/placeholder.jpg";
            }}
          />
          {isSold && (
            <span className="absolute left-3 top-3 z-10 rounded-full bg-red-600 text-white text-xs font-semibold px-2 py-0.5">
              Sold
            </span>
          )}
        </div>
      </Link>

      <div className="p-3">
        <div className="flex items-start justify-between gap-2">
          <Link href={`/rug/${id}`} className="font-medium leading-snug line-clamp-1 hover:underline" title={name}>
            {name}
          </Link>
          {fmtPrice(p.price) && <div className="text-sm font-semibold whitespace-nowrap">{fmtPrice(p.price)}</div>}
        </div>

        <div className="mt-1 text-xs text-gray-600 flex items-center gap-2">
          <span className="line-clamp-1">{p.size_text || "\u2014"}</span>
          <span aria-hidden></span>
          <span className={isSold ? "text-gray-500" : "text-green-600"}>
            {isSold ? "" : "in stock"}
          </span>
        </div>

        <div className="mt-2">
          <button
            onClick={() => (inList ? remove(id) : add({ id, name, image: img, price: p.price }))}
            className={`text-xs px-2.5 py-1.5 rounded-md border ${inList ? "bg-black text-white" : "hover:bg-gray-100"}`}>
            {inList ? "Added" : "+ Add to List"}
          </button>
        </div>
      </div>
    </article>
  );
}

"use client";
import { useEffect, useMemo, useState } from "react";
import ProductCard from "./ProductCard";
import type { Filters } from "./FiltersPanel";
import { safeJson } from "@/lib/safeJson";

function buildQuery(filters?: Filters, category?: string, page = 1, limit = 24, sort?: string) {
  const q = new URLSearchParams();
  q.set("limit", String(limit));
  q.set("page", String(page));
  if (category) q.set("category", category);
  if (sort === "price-asc") q.set("sort", "price:asc");
  if (sort === "price-desc") q.set("sort", "price:desc");
  if (!filters) return q;

  if (filters.stock?.length) q.set("stock", filters.stock.join(","));
  if (filters.collection?.length) q.set("collection", filters.collection.join(","));
  if (filters.design?.length) q.set("design", filters.design.join(","));
  if (filters.color?.length) q.set("color", filters.color.join(","));
  if (filters.size?.length) q.set("size", filters.size.join(","));
  return q;
}

export default function ProductsGrid({ category, filters }: { category?: string; filters?: Filters }) {
  const [items, setItems] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [sort, setSort] = useState<string>("featured");
  const [page, setPage] = useState<number>(1);
  const [total, setTotal] = useState<number>(0);
  const limit = 24;

  useEffect(() => { setPage(1); }, [category, JSON.stringify(filters), sort]);

  useEffect(() => {
    const q = buildQuery(filters, category, page, limit, sort);
    const url = `/admin-api/products?${q.toString()}`;
    let alive = true;
    (async () => {
      setLoading(true);
      try {
        const res = await fetch(url, { cache: "no-store", headers: { Accept: "application/json" } });
        const data = await safeJson(res, { items: [], total: 0 });
        if (!alive) return;
        const arr = Array.isArray(data.items) ? data.items : [];
        setItems(arr);
        setTotal(typeof data.total === "number" ? data.total : arr.length);
      } catch {
        if (!alive) return;
        setItems([]); setTotal(0);
      } finally { if (alive) setLoading(false); }
    })();
    return () => { alive = false; };
  }, [category, filters, page, sort]);

  const pageCount = Math.max(1, Math.ceil(total / limit));

  return (
    <div className="flex-1">
      <div className="mb-3 flex items-center justify-between gap-3">
        <div className="text-sm text-gray-600">{total} rugs</div>
        <div className="flex items-center gap-2">
          <select className="border rounded-lg px-2 py-1 text-sm" value={sort} onChange={(e)=>setSort(e.target.value)}>
            <option value="featured">Featured</option>
            <option value="price-asc">Price: Low to High</option>
            <option value="price-desc">Price: High to Low</option>
          </select>
          <div className="flex items-center gap-1">
            <button disabled={page<=1} onClick={()=>setPage(p=>Math.max(1,p-1))}
              className="px-2 py-1 border rounded-md text-sm disabled:opacity-50">Prev</button>
            <span className="text-sm">{page} / {pageCount}</span>
            <button disabled={page>=pageCount} onClick={()=>setPage(p=>Math.min(pageCount,p+1))}
              className="px-2 py-1 border rounded-md text-sm disabled:opacity-50">Next</button>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3">
          {Array.from({ length: 6 }).map((_,i)=>(
            <div key={i} className="rounded-2xl border overflow-hidden bg-white">
              <div className="animate-pulse aspect-[4/3] bg-gray-200" />
              <div className="p-3 space-y-2">
                <div className="h-3 w-3/4 bg-gray-200 rounded" />
                <div className="h-3 w-1/2 bg-gray-200 rounded" />
              </div>
            </div>
          ))}
        </div>
      ) : items.length === 0 ? (
        <div className="py-12 text-center text-gray-600">No products found.</div>
      ) : (
        <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3">
          {items.map(p => <ProductCard key={p._id ?? p.product_id ?? p.id} p={p} />)}
        </div>
      )}
    </div>
  );
}
